import React from 'react';
import { connect } from 'react-redux';
import { Navigate } from 'react-router-dom';
import Login from '../components/Login/Login';
import Posts from '../components/Posts';
import LoginActions from '../actions/LoginAction';

import { useLocation,useEffect } from 'react';

function LoginAuthentication(props){


    const location = useLocation();
    const username=location.state.username;
    useEffect(()=>{

      props.fetchUser(username);

  },[])

    return(
        <>
        if(props.user){
          <Posts />
        }
        else{
          <Login />
        }
        </>

    )
}


const mapStateToProps = (state, ownProps) => ({
  username: state.login.username,
  isLoggedIn: state.session.isAuthenticated
});

const mapDispatchToProps = (dispatch, ownProps) => ({
  onSubmit: (e) => {

    e.target.reset();
    dispatch(LoginActions.sendLoginData(this.props.username));
    dispatch(SessionActions.saveSessionData());
  }
});

export default connect(mapStateToProps, { fetchUsers })(LoginAuthentication);
