import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Navigate} from 'react-router-dom'
import { fetchPostsSelf } from '../../actions/PostAction';
import Postform from '../Postform';
import Comments from '../Comments';


function MyPosts() {
  state={
    active: false

  }
  componentDidMount=()=> {
    this.props.fetchPostsSelf();
  }

  componentWillReceiveProps=(nextProps) =>{
    if (nextProps.newPost) {
      this.props.posts.unshift(nextProps.newPost);
    }
  }



    const postItems = this.props.posts.map(post => (
      <div key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.body}</p>
        <button onClick={this.navigateTo}>View Comments</button>
        {this.state.active && <Comments id={post.id} />}
      </div>
    ));
    return (

      <div>

        <h1>Posts</h1>
        {postItems}

      </div>
    );
  }


MyPosts.propTypes = {
  fetchPostsSelf: PropTypes.func.isRequired,
  posts: PropTypes.array.isRequired,
  newPost: PropTypes.object
};

const mapStateToProps = state => ({
  posts: state.posts.myps,
  newPost: state.posts.item
});

export default connect(mapStateToProps, { fetchPostsSelf })(MyPosts);
