import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchComments } from '../actions/CommentAction';
import { useEffect } from 'react';

function Comments (props) {

  useEffect(()=> {
    this.props.fetchComments(1);
  },[])

 

  {props.newComment && props.comments.push(props.newComment)}

 
    const commentItems = this.props.comments.map(c => (
      <div key={c.id}>
        <h3>{c.name}</h3>
        <p>{c.body}</p>
      </div>
    ));
    return (
      <div>
        <h1>Comments</h1>
        {commentItems}
      </div>
    );
  }


Comments.propTypes = {
    fetchComments: PropTypes.func.isRequired,
    comments: PropTypes.array.isRequired,
  newComment: PropTypes.object
};

const mapStateToProps = state => ({
  comments: state.posts.comments,
  newComment: state.posts.comment
});

export default connect(mapStateToProps, { fetchComments })(Comments);
