import React from 'react';
import {BrowserRouter as Router,Routes, Route, Navigate, } from 'react-router-dom';
import Navbar from '../components/Navbar/Navbar';
import PostDisplay from '../components/PostDisplay';
import CommentsDisplay from '../components/CommentsDisplay';
import LoginAuthentication from '../components/LoginAuthentication';
import { useLocation } from 'react-router-dom';

const PrivateRoute = ({ component: Component, path1: path1, session, ...rest}) => {
  
  const isValidSession = (props) => {
    if (session.isAuthenticated) {
      return (<Component {...props} />);
    }
    //return (<Navigate to={{ pathname: '/login', state: { from: path1 } }} />);
  };
  return (<Route {...rest} render={isValidSession} />);
};

const AppRoutes = ({ session },{path1}) => (
  
  <Router>
     <Navbar />
      
    <PrivateRoute exact path="/posts" component={PostDisplay}  path1={path1} session={session}/>
    <Route path="/login" component={LoginAuthentication }/>
    <PrivateRoute path="/comments" component={CommentsDisplay} path1={path1}  session={session}/>
    
  </Router>
);

export default AppRoutes;
