import {Component} from 'react';

import { Provider } from 'react-redux';
import Comments from './Comments';
import CommentForm from './CommentForm';


function CommentsDisplay(){


    const { id } = useParams();
    return(
        <>
        <CommentForm cid={id}/>
        <Comments />
        </>

    )
}

export default CommentsDisplay;
