import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import {Link} from 'react-router-dom'
import { fetchPostsSelf } from '../../actions/PostAction';
import Postform from '../Postform';
import Comments from '../Comments';
import {useEffect} from 'react';


function MyPosts(props) {

  useEffect(()=> {
    props.fetchPostsSelf();
    if (newPost) {
      props.posts.unshift(newPost);
    }

  },[])

  const componentWillReceiveProps=(nextProps) =>{
    if (nextProps.newPost) {
      props.posts.unshift(nextProps.newPost);
    }
  }
  const postItems = props.posts.map(post => (
      <div key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.body}</p>
        <Link to='/comments' state={post.id}>View Comments</Link>

      </div>
    ));
    return (

      <div>

        <h1>Posts</h1>
        {postItems}

      </div>
    );
  }


MyPosts.propTypes = {
  fetchPostsSelf: PropTypes.func.isRequired,
  posts: PropTypes.array.isRequired,
  newPost: PropTypes.object
};

const mapStateToProps = state => ({
  posts: state.posts.myps,
  newPost: state.posts.item
});

export default connect(mapStateToProps, { fetchPostsSelf })(MyPosts);
